import React, { useEffect, useState } from "react";
import Grid from '@mui/material/Grid2';
import Divider from '@mui/material/Divider';
import { Stack } from "@mui/material";
import PrayerCard from "./PrayerCard";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import axios from 'axios'
import moment from "moment"
// https://momentjs.com/


export default function MainContent(){
    const [timings,setTiming]=useState({Fajr:"",Dhuhr:"",Asr:"",Maghrib:"",Isha:""})
    const [city,setCity]=useState({displayName:"القاهرة",apiName:"cairo"})
    const [today,setToday]=useState("")
    const [nextPrayerIndex,setNextPrayerIndex]=useState(2)
    const [remainingTimeState,setRemainingTimeState]=useState("")

    const cities=[{displayName:"القاهرة",apiName:"cairo"},{displayName:"اسوان",apiName:"Aswan"},{displayName:"الاسكندرية",apiName:"Alexandria"}]
    const prayersArray=[{key:"Fajr",displayName:"الفجر"},{key:"Dhuhr",displayName:"الظهر"},{key:"Asr",displayName:"العصر"},{key:"Maghrib",displayName:"المغرب"},{key:"Isha",displayName:"العشاء"}]
    
    // get the date of today for the api
    const date=moment()
    const dateFormated=date.format("DD-MM-YYYY")

    const getData=async ()=>{
        const response= await axios.get(`https://api.aladhan.com/v1/timingsByCity/${dateFormated}?country=EGY&city=${city.apiName}`) 
        setTiming(response.data.data.timings)
    }

    // is exected once when rendering and when city changed
    useEffect( ()=>{
        getData()
        
    },[city])

    // is exected once when rendering and when city changed 
    useEffect(()=>{
        const t=moment()
        setToday(t.format("MMM Do YYYY | hh:mm"))

        // to calculate the time remaining ever second
        let interval=setInterval(()=>{
            setUpCountDownTimer()
        },1000)

        return () =>{
            clearInterval(interval)
        }
    },[timings,city])


    const setUpCountDownTimer=()=>{
        let momentNow=moment()

        // const Isha=timings["Isha"]
        // const IshaMomentObject=moment(Isha,"hh:mm") // converting Isha from string to moment object to be able to use isBefore function
        // console.log(momentNow.isBefore(IshaMomentObject))

        let prayerIndex=null
        if(momentNow.isAfter(moment(timings.Fajr,"hh:mm"))&&momentNow.isBefore(moment(timings.Dhuhr,"hh:mm"))){
            prayerIndex=1
        }else if(momentNow.isAfter(moment(timings.Dhuhr,"hh:mm"))&&momentNow.isBefore(moment(timings.Asr,"hh:mm"))){
            prayerIndex=2
        }else if(momentNow.isAfter(moment(timings.Asr,"hh:mm"))&&momentNow.isBefore(moment(timings.Maghrib,"hh:mm"))){
            prayerIndex=3
        }else if(momentNow.isAfter(moment(timings.Maghrib,"hh:mm"))&&momentNow.isBefore(moment(timings.Isha,"hh:mm"))){
            prayerIndex=4
        }else{
            prayerIndex=0
        }
        setNextPrayerIndex(prayerIndex)


        // now after knowing what is the next prayer , we can setup the countdown timer by getting the next prayer's time
        const nextPrayerObject=prayersArray[prayerIndex]
        const nextPrayerTime=timings[nextPrayerObject.key]
        const nextPrayerTimeObj=moment(nextPrayerTime,"hh:mm")

        let remainingTime=nextPrayerTimeObj.diff(momentNow)

        //  if the next prayer is fajr the remaining time will be negative because for example we do 04:45 - 20:30
        if (remainingTime<0){
            // from now to midnight
            const midNightDiff=moment("23:59","hh:mm").diff(momentNow)
            // from midnight to fajr
            const fajrMidnightDiff=nextPrayerTimeObj.diff(moment("00:00","hh:mm"))

            const totalDiff=midNightDiff+fajrMidnightDiff
            remainingTime=totalDiff
        }
        // console.log(remainingTime)  remaining time in seconds 

        // converting the remaining time to duration to kow the remaining time in hours:minutes:seconds
        const remainingTimeDuration=moment.duration(remainingTime)

        setRemainingTimeState(`${remainingTimeDuration.seconds()} : ${remainingTimeDuration.minutes()} : ${remainingTimeDuration.hours()}`)
        
        // console.log(remainingTimeDuration.hours(),remainingTimeDuration.minutes(),remainingTimeDuration.seconds())



    }

    const handleCityChange = (event) => {
        setCity(event.target.value)
       
        // another solution using higher order function "find" but with this solution i have to change the value of <MenuItem> to city.apiName instead of city
        // let selectedCity=cities.find((city)=>city.apiName===event.target.value)
        // setCity(selectedCity);
      };





    return(
        <div style={{color:"white",width:"100%",height:"fit-content"}} >

            {/* top row */}
            <Grid container >
                {/* mn awel xs le akbar yb2a el 6 columns  */}
                <Grid size={{xs:6}} style={{textAlign:"right"}}>  
                    <div>
                        <h2> {today}</h2>
                        <h1>{city.displayName}</h1>
                    </div>
                </Grid>
                <Grid size={{xs:6}} style={{textAlign:"right"}}>
                    <div>
                        <h2> متبقي حتى صلاة {prayersArray[nextPrayerIndex].displayName}</h2>
                        <h1>{remainingTimeState}</h1>
                    </div>
                </Grid>
            </Grid>
            {/* top row */}


            <Divider style={{borderColor:"white",opacity:"0.2"}}/>

            {/* prayers cards */}

            <Stack direction="row" justifyContent={"space-around"} style={{marginTop:"50px"}}>
                <PrayerCard name="الفجر" time={timings.Fajr} image="data:image/jpeg;base64,"/>
                <PrayerCard name="الظهر" time={timings.Dhuhr} image="data:image/jpeg;base64,"/>
                <PrayerCard name="العصر" time={timings.Asr} image="data:image/jpeg;base64,"/>
                <PrayerCard name="المغرب" time={timings.Maghrib} image="data:image/jpeg;base64,"/>
                <PrayerCard name="العشاء" time={timings.Isha} image="data:image/jpeg;base64,"/>
            </Stack>
            {/* prayers cards */}

            {/* select menu */}

                <Stack style={{marginTop:"20px"}} justifyContent="center" direction="row">
                    <FormControl style={{width:"20%",color:"white"}}>
                        <InputLabel style={{color:"white" ,borderColor:"white"}} id="demo-simple-select-label">المدينة</InputLabel>
                        <Select style={{color:"white"}}
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={city}
                        label="city"
                        onChange={handleCityChange}
                        >
                        {cities.map((city)=>{
                         return <MenuItem  value={city}>{city.displayName}</MenuItem>
                        })}
                        </Select>
                    </FormControl>
                    
                </Stack>

            {/* select menu */}


        </div>
    )
}